allow:
Bash(# 4195行目以前を保存
sed -n ''1,4194p'' /mnt/d/クラウドパートナーHP/spine-positioning-system-minimal.js > /tmp/before.js

# 修正版のsetupCharacterTabEvents関数を作成
cat << ''EOF'' > /tmp/new_function.js
function setupCharacterTabEvents() {
    const contentArea = document.getElementById(''slide-menu-content'');
    if (!contentArea) return;

    // 既存のsetupCharacterPanelEventsを利用して、統合メニューでもドラッグ&ドロップを有効化
    setupCharacterPanelEvents(contentArea);

    // 統合メニュー特有のレイヤーボタンイベントのみ設定
    contentArea.querySelectorAll(''.layer-btn'').forEach(btn => {
        btn.addEventListener(''click'', (e) => {
            e.stopPropagation();

            const action = btn.dataset.action;
            const index = parseInt(btn.dataset.index);

            if (action === ''up'') {
                moveCharacterLayer(index, ''up'');
            } else if (action === ''down'') {
                moveCharacterLayer(index, ''down'');
            }

            updateSlideMenuContent(); // レイヤー変更後にコンテンツ更新
        });
    });
}
EOF

# 4370行目以降を保存
sed -n ''4370,$p'' /mnt/d/クラウドパートナーHP/spine-positioning-system-minimal.js > /tmp/after.js

# 3つのファイルを結合
cat /tmp/before.js /tmp/new_function.js /tmp/after.js > /mnt/d/クラウドパートナーHP/spine-positioning-system-minimal.js

echo ""関数を修正しました"")
